import styled from "styled-components";
import Button from "../../../components/functional/Button";

const Section = styled.div`
  width: 100vw;
  overflow: hidden;
  @media (max-width: 600px) {
    height: 50vh;
    width: 100%;
  }
`;

const BackgroundBlock = styled.div`
  height: 150%;
  width: 100%;
  transform: rotate(4deg);
  transform-origin: left;
  border-radius: 90px 0px 0px 0px;
  background: linear-gradient(
    208.89deg,
    var(--thm-primary-color) 1.69%,
    var(--thm-secondary-color) 63.59%
  );
  z-index: -10;
  position: absolute;
  left: 0px;
  top: 0px;
`;

const Container = styled.div`
  margin-top: 100px;
  display: flex;
  justify-content: space-between;
  align-items: top;

  & > div {
    display: flex;
    flex-direction: column;
    margin: 40px;
    width: 50%;

    ul {
      display: flex;
      align-items: center;
      list-style: none;
      font-weight: 600;
    }

    .menu_container {
      display: flex;

      svg {
        width: 80px;
        height: 80px;
        padding: 10px 20px 10px 0px;
      }

      li {
        padding: 10px 20px;
      }

      & + div {
        display: flex;
        gap: 30px;
        padding: 50px 0px;
        input {
          padding: 10px;
          background: transparent;
          border-radius: 3px;
          outline: none;
          border: 1px solid white;
          color: white;
        }

        & + div {
          padding: 30px 0px;
          font-size: 13px;
          text-decoration: underline;

          p {
            margin-right: 15px;
          }
        }
      }
    }

    .dual_list {
      display: flex;
      justify-content: space-between;
      align-items: center;
      width: 100%;

      li {
        margin-right: 20px;

        svg: {
          width: 50px;
          height: 50px;
        }
      }

      & + div {
        margin-top: 50px;
        font-size: 12px;
        width: 100%;
      }
    }
  }
`;

